import React, { useState, useEffect } from 'react';
import Toolbar from "../components/Toolbar";

const GalleryPage = () => {
    const [photos, setPhotos] = useState([]);

    // Fetching data from the API
    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((res) => res.json())
            .then((data) => {
                console.log(data); // Check the structure of the data
                setPhotos(data);
            })
            .catch(error => console.error("Error fetching data:", error)); // Handle any errors
    }, []);

    return (
        <div>
            <Toolbar className="page-header d-flex align-items-center" />
            <h2>Products</h2>
            <div>
                {photos.length > 0 ? (
                    photos.map((photo) => (
                        <div key={photo.id}>
                            {/* Ensure 'photo.image' is the correct field */}
                            <img
                                src={photo.image} // Corrected property name
                                alt={photo.title}
                                width={100}
                                style={{ margin: '10px' }} // Optional: styling
                            />
                            <h4>{photo.title}</h4> {/* Display product title */}
                        </div>
                    ))
                ) : (
                    <p>Loading...</p> // Show loading message while fetching
                )}
            </div>
        </div>
    );
};

export default GalleryPage;
