import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const UserDetailPage = () => {
    const { id } = useParams();  // Retrieve user ID from the URL
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
            .then(response => response.json())
            .then(data => {
                setUser(data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Error fetching user details:', error);
                setLoading(false);
            });
    }, [id]);

    if (loading) {
        return <p>Loading user details...</p>;
    }

    if (!user) {
        return <p>User not found.</p>;
    }

    return (
        <div className="user-detail-page">
            <button onClick={() => history.push('/')}>Back to User List</button>
            <h1>{user.name}</h1>
            <p><strong>Username:</strong> {user.username}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Phone:</strong> {user.phone}</p>
            <p><strong>Website:</strong> <a href={`https://${user.website}`} target="_blank" rel="noopener noreferrer">{user.website}</a></p>
        </div>
    );
};

export default UserDetailPage;
