import React, { useState } from 'react';
import useStore from '../store/main';  // Import the Zustand store

const ProfilePage = () => {
    // Get the setUserData function from the store
    const { setUserData } = useStore();

    // Local state for input fields
    const [username, setUsername] = useState('');
    const [profileImageUrl, setProfileImageUrl] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        // Save the user data (username and image URL) to Zustand store
        setUserData(username, profileImageUrl);
        alert('Profile data saved!');
    };

    return (
        <div className="profile-page">
            <h2>Set Your Profile</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="username">Username</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter your username"
                    />
                </div>

                <div>
                    <label htmlFor="profileImageUrl">Profile Image URL</label>
                    <input
                        type="text"
                        id="profileImageUrl"
                        value={profileImageUrl}
                        onChange={(e) => setProfileImageUrl(e.target.value)}
                        placeholder="Enter profile image URL"
                    />
                </div>

                <button type="submit">Save Profile</button>
            </form>

            <h3>Preview Profile</h3>
            {username && profileImageUrl ? (
                <div>
                    <p><strong>Username:</strong> {username}</p>
                    <img src={profileImageUrl} alt="Profile" style={{ width: '100px', height: '100px', borderRadius: '50%' }} />
                </div>
            ) : (
                <p>No profile data to display</p>
            )}
        </div>
    );
};

export default ProfilePage;
