import React, { useEffect, useState } from 'react';

// This will display each user's info
const UserPage = () => {
    const [users, setUsers] = useState([]); // State to store users

    // Fetch users data when component mounts
    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json()) // Convert to JSON
            .then(data => setUsers(data)) // Set users in state
            .catch(error => console.error('Error fetching data:', error)); // Error handling
    }, []); // Empty array ensures it runs only once when component mounts

    return (
        <div className="user-page">
            <h1>User List</h1>
            <div className="user-cards">
                {/* Map through all users and display each user in a card */}
                {users.length > 0 ? (
                    users.map(user => (
                        <div key={user.id} className="user-card">
                            <h2>{user.name}</h2>
                            <p><strong>Username:</strong> {user.username}</p>
                            <p><strong>Email:</strong> {user.email}</p>
                            <p><strong>Phone:</strong> {user.phone}</p>
                            <p><strong>Website:</strong> <a href={`https://${user.website}`} target="_blank" rel="noopener noreferrer">{user.website}</a></p>
                        </div>
                    ))
                ) : (
                    <p>Loading users...</p> // Show loading text if data is not fetched yet
                )}
            </div>
        </div>
    );
}

export default UserPage;
