import React, { useState } from 'react';
import useStore from '../store/main';

const MessageModal = ({ isOpen, onClose, receiverUsername }) => {
    const [message, setMessage] = useState('');
    const { addMessage, username } = useStore();

    const handleSendMessage = () => {
        // Add the message to the store
        addMessage(username, receiverUsername, message);
        // Close the modal after sending the message
        onClose();
        setMessage(''); // Reset the message input
    };

    if (!isOpen) return null;

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal" onClick={(e) => e.stopPropagation()}>
                <h3>Send Message to {receiverUsername}</h3>
                <textarea
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="Type your message..."
                    rows="4"
                    style={{ width: '100%' }}
                />
                <div className="modal-actions">
                    <button onClick={handleSendMessage}>Send</button>
                    <button onClick={onClose}>Cancel</button>
                </div>
            </div>
        </div>
    );
};

export default MessageModal;
