import { create } from 'zustand';

const useStore = create((set) => ({
    // User data for registration
    username: '',
    password1: '',
    password2: '',

    // Store for posts
    posts: [],

    // Store for users
    allUsers: [],

    // Store for messages (global messages, not specific to users)
    messages: [],

    // Active user for chat
    activeUserId: null,

    // Set user data in store (called after login or profile update)
    setUserData: (username, profileImageUrl) =>
        set((state) => ({
            allUsers: [
                ...state.allUsers,
                { id: Date.now(), username, profileImageUrl, messages: [] } // Adding unique `id` for users
            ]
        })),

    // Action to update registration data
    setRegistrationData: (username, password1, password2) =>
        set({ username, password1, password2 }),

    // Add a message to the store (specific user messages)
    addMessage: (sender, receiver, message) =>
        set((state) => ({
            messages: [
                ...state.messages,
                { sender, receiver, message, timestamp: Date.now() }
            ]
        })),

    // Add post action
    addPost: (description, imageUrl) => set((state) => ({
        posts: [
            ...state.posts,
            {
                id: Date.now(),
                description,
                imageUrl,
                username: state.username
            }
        ]
    })),

    // Delete post action
    deletePost: (postId) => set((state) => ({
        posts: state.posts.filter((post) => post.id !== postId)
    })),

    // Send a message to a specific user
    sendMessage: (userId, message) => {
        set((state) => {
            const users = state.allUsers.map(user =>
                user.id === userId
                    ? { ...user, messages: [...user.messages, { text: message, sender: 'You' }] }
                    : user
            );
            return { allUsers: users };
        });
    },

    // Set the active user for the chat (this will be used in MessagePage)
    setActiveUser: (userId) => {
        set({ activeUserId: userId });
    }
}));

export default useStore;
