import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Toolbar from "../components/Toolbar";

const PhotosPage = () => {
    const { id } = useParams();  // Get the album ID from the URL
    const [photos, setPhotos] = useState([]);  // Initialize state for the list of photos

    useEffect(() => {
        // Fetch photos for the album with the given ID
        fetch(`https://jsonplaceholder.typicode.com/photos?albumId=${id}`)
            .then((res) => res.json())
            .then((data) => {
                setPhotos(data);  // Store the fetched photos
            })
            .catch((error) => {
                console.error("Error fetching photos:", error);
            });
    }, [id]);  // Re-run the effect when 'id' changes

    if (photos.length === 0) {
        return <p>Loading photos...</p>;  // Show loading message while fetching photos
    }

    return (
        <div>
            <h2>
                <Toolbar/>
            </h2>
            <h1>Photos</h1>
            <div className="photos-page">
                {/* Loop through the photos and render each one */}
                {photos.map((photo) => (
                    <div key={photo.id} className="photo-card">
                        <h3 className="mrg">{photo.title}</h3>
                        <img src={photo.url} alt={photo.title} className="photo-img"/>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PhotosPage;
