import React, { useState, useEffect } from 'react';
import Toolbar from "../components/Toolbar";

const GalleryPage = () => {
    const [ photos, setPhotos] = useState([]);

    /////////////////Fetching
    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((res) => res.json())
            .then((data) => {
                console.log(data)
                setPhotos(data);
            })
            .catch(error => console.error("Error fetching data:", error)); // Handle any errors
    }, []);

    return (
        <div>
            <Toolbar className="page-header d-flex align-items-center" />
            <h2>Products</h2>
            <div className="page-content border-3">
                {photos.length > 0 ? (
                    photos.map((photo) => (
                        <div key={photo.id}>

                            <img className="border-2"
                                src={photo.image}
                                alt={photo.title}
                                width={100}
                                style={{ margin: '10px' }}
                            />
                            <h4>{photo.title}</h4> {}
                            <button onClick={() => setPhotos(photos)}>{photo.id}</button>
                        </div>
                    ))
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
};

export default GalleryPage;
