import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Toolbar from "../components/Toolbar";

const AlbumsPage = () => {
    const { id } = useParams();  // Get the 'id' from the URL params
    const [albums, setAlbums] = useState([]);  // State to hold the albums for the user

    useEffect(() => {
        // Fetch all albums for the specific user
        fetch(`https://jsonplaceholder.typicode.com/albums?userId=${id}`)
            .then(response => response.json())
            .then(data => {
                setAlbums(data);  // Set the fetched albums
            })
            .catch(error => {
                console.error('Error fetching albums:', error);
            });
    }, [id]);  // Re-run when 'id' changes (i.e., when a different user is selected)

    if (albums.length === 0) {
        return <p>Loading albums...</p>;  // Show a loading message while the albums are being fetched
    }

    return (
        <div className="text-center d-flex flex-column align-items-center">
            <h2>
                <Toolbar />
            </h2>
            <div className="main-user-card d-flex flex-row align-items-center user-cont">
                <div>
                    <h3 className="user5">Albums for User {id}</h3>
                    {albums.map((album) => (
                        <div key={album.id} className="album-card">
                            <p><strong>Album ID:</strong> {album.id}</p>
                            <p><strong>Title:</strong> {album.title}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default AlbumsPage;
