import React, { useState } from 'react';
import useStore from '../store/main';
import Toolbar from "../components/Toolbar"


const CreatePostPage = () => {
    const { setUserData } = useStore();
    const [username, setUsername] = useState('');
    const [profileImageUrl, setProfileImageUrl] = useState('');
    const handleSubmit = (e) => {
        e.preventDefault();
        setUserData(username, profileImageUrl);
        alert('Profile data saved!');
    };

    return (
        <div className="profile-page">
            <h2>
                <Toolbar/>
            </h2>
            <h2>Create Your Post</h2>
            <form onSubmit={handleSubmit}>


                <div className="input-cont2">
                    <label className="text-bg-danger" htmlFor="profileImageUrl">Post Image URL</label>
                    <input
                        type="text"
                        id="profileImageUrl"
                        value={profileImageUrl}
                        onChange={(e) => setProfileImageUrl(e.target.value)}
                        placeholder="Enter image URL"
                    />
                </div>

                <div className="input-cont2">
                    <label className="text-bg-danger" htmlFor="username">Description</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Enter description"
                    />
                </div>

                <button className="" type="submit">Create Post</button>
            </form>
        </div>
    );
};

export default CreatePostPage;
