import React, { useState } from "react";

const RegisterPage = () => {
    // State to store form input values
    const [username, setUsername] = useState("");
    const [passwordOne, setPasswordOne] = useState("");
    const [passwordTwo, setPasswordTwo] = useState("");
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);

        // Validate passwords match
        if (passwordOne !== passwordTwo) {
            setError("Passwords do not match");
            setLoading(false);
            return;
        }

        const registerData = {
            name: username,
            passwordOne: passwordOne,
            passwordTwo: passwordTwo,
        };

        // Send POST request to the server
        try {
            const response = await fetch("http://167.99.138.67:1111/createaccount", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(registerData),
            });

            if (response.ok) {
                // Handle successful response
                alert("Registration successful!");
                // Optionally, redirect or do something else
            } else {
                // Handle error response
                const errorMessage = await response.text();
                setError(errorMessage || "Failed to create account.");
            }
        } catch (err) {
            setError("Something went wrong. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2>Register</h2>
                <form onSubmit={handleSubmit}>
                    <div style={styles.inputGroup}>
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            id="username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                            required
                        />
                    </div>

                    <div style={styles.inputGroup}>
                        <label htmlFor="passwordOne">Password</label>
                        <input
                            type="password"
                            id="passwordOne"
                            value={passwordOne}
                            onChange={(e) => setPasswordOne(e.target.value)}
                            required
                        />
                    </div>

                    <div style={styles.inputGroup}>
                        <label htmlFor="passwordTwo">Confirm Password</label>
                        <input
                            type="password"
                            id="passwordTwo"
                            value={passwordTwo}
                            onChange={(e) => setPasswordTwo(e.target.value)}
                            required
                        />
                    </div>

                    <div style={styles.error}>
                        {error && <p style={{ color: "red" }}>{error}</p>}
                    </div>

                    <button type="submit" disabled={loading} style={styles.button}>
                        {loading ? "Registering..." : "Register"}
                    </button>
                </form>
            </div>
        </div>
    );
};

// Simple styles (you can adjust these or use CSS modules)
const styles = {
    container: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
        backgroundColor: "#f4f4f9",
    },
    card: {
        background: "#fff",
        padding: "30px",
        borderRadius: "8px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        width: "300px",
        textAlign: "center",
    },
    inputGroup: {
        marginBottom: "15px",
        textAlign: "left",
    },
    input: {
        width: "100%",
        padding: "8px",
        borderRadius: "4px",
        border: "1px solid #ccc",
    },
    button: {
        width: "100%",
        padding: "10px",
        backgroundColor: "#007bff",
        color: "#fff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
    },
    error: {
        marginTop: "10px",
        fontSize: "14px",
    },
};

export default RegisterPage;
