import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Toolbar from "../components/Toolbar";

const PostsPage = () => {
    const { id } = useParams(); // Extract the 'id' from the URL
    const [posts, setPosts] = useState([]); // Rename state variable to 'post'

    useEffect(() => {
        // Fetch post details based on the ID
        fetch(`https://jsonplaceholder.typicode.com/posts?userId=${id}`)

            .then(response => response.json())
            .then(data => {
                setPosts(data); // Store fetched data in 'post' state
            })
            .catch(error => {
                console.error('Error fetching post details:', error);
            });
    }, [id]); // Re-fetch data when the 'id' changes

    // Show loading or error message if post is not found
    if (!posts) {
        return <p>Loading post...</p>;
    }

    return (
        <div className="text-center d-flex flex-column align-items-center">
            <h2>
                <Toolbar />
            </h2>
            <div className="main-user-card d-flex flex-row align-items-center user-cont1">
                <div>
                   <div>
                       {posts.map(post => (
                           <div key={post.id} className="post-card">
                               <h7>{post.title}</h7>
                               <p>{post.body}</p>
                           </div>
                       ))}
                   </div>
                </div>
            </div>
        </div>
    );
};

export default PostsPage;
