import React, { useState, useEffect } from 'react';
import Toolbar from "../components/Toolbar";
const [cartTotal, setCartTotal] = useState(0);

const GalleryPage = () => {
    const [ photos, setPhotos] = useState([]);

    /////////////////Fetching
    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((res) => res.json())
            .then((data) => {
                console.log(data)
                setPhotos(data);
            })
            .catch(error => console.error("Error fetching data:", error)); // Handle any errors
    }, []);

    return (
        <div>
            <Toolbar className="page-header d-flex align-items-center" />
            <h2>Products</h2>
            <h3>Total money spend: ${cartTotal.toFixed(2)} </h3>
            <div className="page-content border-3">
                {photos.length > 0 ? (
                    photos.map((photo) => (
                        <div>
                        <div key={photo.id}>
                            <div className="items">
                                <img className="border-2"
                                     src={photo.image}
                                     alt={photo.title}
                                     width={100}
                                     style={{margin: '10px'}}
                                />
                                <h4>{photo.title}</h4>
                                <p>Price: ${photo.price}</p>
                                <button onClick={() => handleAddToCart(photo.price)}>Add to cart</button>
                            </div>
                        </div>
                        </div>
                    ))
                ) : (
                    <p>Loading...</p>
                )}
            </div>
        </div>
    );
};

export default GalleryPage;
