import React, {useRef, useState} from "react";
import {Link, useNavigate} from "react-router-dom";


const UploadPage = ({secret}) => {
    const titleRef = useRef()
    const descriptionRef = useRef()
    const imageRef = useRef()

    const nav = useNavigate()

    const [error, setError] = useState(null)

    function upload() {
        const post = {
            image: imageRef.current.value,
            description: descriptionRef.current.value,
            title: titleRef.current.value,
            secretKey: secret
        }

        const options = {
            method: "POST",
            headers: {
                "content-type": "application/json"
            },
            body: JSON.stringify(post)
        }

        fetch("http://167.99.138.67:1111/createpost", options)
            .then(res => res.json())
            .then(response => {
                console.log(response)
                if (response.success) {
                    setError(null)

                } else {
                    setError(response.message)
                }
            })
    }

    return (
        <div>
            <div className="toolbar-container">
                <Link to="/gallery" className="text-white">HOME</Link>
                <Link to="/profile" className="text-white">Users</Link>
            </div>
            <div style={styles.container}>
                <div style={styles.card}>
                    <h2>Create Post</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="d-flex row" style={styles.inputGroup}>
                            <label htmlFor="title">Title</label>
                            <input
                                type="text"
                                id="title"
                                value={title}
                                onChange={(e) => setTitle(e.target.value)}
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label htmlFor="description">Description</label>
                            <textarea
                                id="description"
                                value={description}
                                onChange={(e) => setDescription(e.target.value)}
                                required
                            />
                        </div>
                        <div style={styles.inputGroup}>
                            <label htmlFor="image">Upload Image</label>
                            <input
                                type="file"
                                id="image"
                                accept="image/*"
                                onChange={handleImageChange}
                                required
                            />
                        </div>



                        <button type="submit" disabled={loading} style={styles.button}>
                            {loading ? "Creating Post..." : "Create Post"}
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
};

const styles = {
    container: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "auto",
        textAlign: "center",
    },
    card: {
        background: "#fff",
        padding: "30px",
        borderRadius: "8px",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        width: "300px",
        textAlign: "center",
    },
    inputGroup: {
        marginBottom: "15px",
        textAlign: "center",
    },
    input: {
        width: "100%",
        padding: "8px",
        borderRadius: "4px",
        border: "1px solid #ccc",
    },
    button: {
        width: "100%",
        padding: "10px",
        backgroundColor: "#007bff",
        color: "#fff",
        border: "none",
        borderRadius: "4px",
        cursor: "pointer",
    },
    error: {
        marginTop: "10px",
        fontSize: "14px",
    },
};

export default UploadPage;
