import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import useStore from '../store/main';

const MessagePage = () => {
    const { userId } = useParams(); // Get the userId from the URL
    const { allUsers, activeUserId, sendMessage, setActiveUser } = useStore();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');

    useEffect(() => {
        const user = allUsers.find(user => user.id === parseInt(userId));
        if (user) {
            setMessages(user.messages);
        }
    }, [userId, allUsers]);

    useEffect(() => {
        // Set the active user on page load (to make sure it's synced)
        setActiveUser(parseInt(userId));
    }, [userId, setActiveUser]);

    const handleSendMessage = () => {
        if (newMessage.trim()) {
            // Add new message to the store
            sendMessage(parseInt(userId), newMessage);

            // Update the message history in local state
            setMessages(prevMessages => [
                ...prevMessages,
                { text: newMessage, sender: 'You' }
            ]);

            // Clear the input
            setNewMessage('');
        }
    };

    return (
        <div className="message-page">
            <h2>Chat with {allUsers.find(user => user.id === parseInt(userId))?.username}</h2>
            <div className="messages-container">
                {messages.length === 0 ? (
                    <p>No messages yet. Start chatting!</p>
                ) : (
                    <div className="message-history">
                        {messages.map((message, index) => (
                            <div
                                key={index}
                                className={`message ${message.sender === 'You' ? 'sent' : 'received'}`}
                            >
                                <p>{message.text}</p>
                            </div>
                        ))}
                    </div>
                )}
            </div>
            <div className="message-input">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                />
                <button onClick={handleSendMessage}>Send</button>
            </div>
        </div>
    );
};

export default MessagePage;
