import { create } from 'zustand';

const useStore = create((set) => ({
    username: '',
    password1: '',
    password2: '',

    // Action to update username, password1 and password2
    setRegistrationData: (username, password1, password2) => set({ username, password1, password2 }),
    posts: [],  // Store for all posts
    setUserData: (username, profileImageUrl) => set({ userData: { username, profileImageUrl } }),
    addPost: (description, imageUrl) => set((state) => ({
        posts: [...state.posts, { description, imageUrl }]
    })),

}));

export default useStore;


