import React, { useState, useEffect } from 'react';
import Toolbar from "../components/Toolbar";


const GalleryPage = () => {
    const [photos, setPhotos] = useState([]);
    const [cartTotal, setCartTotal] = useState(0);  // This will hold the total price of items added to the cart

    // Fetching data from the API
    useEffect(() => {
        fetch('https://fakestoreapi.com/products')
            .then((res) => res.json())
            .then((data) => {
                console.log(data);
                setPhotos(data);  // Set the photos (products) state
            })
            .catch(error => console.error("Error fetching data:", error)); // Handle any errors
    }, []);

    // Handle the "Add to cart" button click
    const handleAddToCart = (price) => {
        setCartTotal((prevTotal) => prevTotal + price);  // Add the clicked item's price to the total
    };

    return (
        <div>
            <Toolbar className="page-header d-flex align-items-center" />
            <h2>Products</h2>
            <div className="page-content border-3">
                {photos.length > 0 ? (
                    photos.map((photo) => (
                        <div key={photo.id}>
                            <div className="items">
                                <img
                                    className="border-2"
                                    src={photo.image}
                                    alt={photo.title}
                                    width={100}
                                    style={{ margin: '10px' }}
                                />
                                <h4>{photo.title}</h4>
                                <p>Price: ${photo.price}</p>
                                <button onClick={() => handleAddToCart(photo.price)}>Add to cart</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Loading...</p>
                )}
            </div>

            {/* Display total cart value */}
            <div className="cart-total">
                <h3>Cart Total: ${cartTotal.toFixed(2)}</h3>
            </div>
        </div>
    );
};

export default GalleryPage;
