import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import Toolbar from "../components/Toolbar";

const PhotosPage = () => {
    const { id } = useParams();  // Get the post ID from the URL
    const [post, setPost] = useState(null);  // Store a single post

    useEffect(() => {
        // Fetch a specific post based on the 'id'
        fetch(`https://jsonplaceholder.typicode.com/photos${id}`)
            .then((res) => res.json())
            .then((data) => {
                setPost(data);  // Set the fetched post
            })
            .catch((error) => {
                console.error("Error fetching post data:", error);
            });
    }, [id]);  // Re-run the effect when the 'id' changes

    if (!post) {
        return <p>Loading post...</p>;  // Show loading state until the post is fetched
    }

    return (
        <div>
            <h2>
                <Toolbar />
            </h2>
            <div className="user6">
                <div key={post.id} className="post-card">
                    <h5 className="mrg">{post.title}</h5>
                    <p className="mrg">By User {post.userId}</p>
                    <p className="mrg">{post.body}</p>
                </div>
            </div>
        </div>
    )
}

export default PhotosPage;
