import { create } from 'zustand';

const useStore = create((set) => ({
    username: '',
    password1: '',
    password2: '',

    // Store for posts, initialized as empty
    posts: [],

    // Store for user data
    userData: { username: '', profileImageUrl: '' },



    // Set user data in store (called after login)
    setUserData: (username, profileImageUrl) => set({ userData: { username, profileImageUrl } }),

    // Action to update username, password1 and password2
    setRegistrationData: (username, password1, password2) => set({ username, password1, password2 }),

    // Add post action
    addPost: (description, imageUrl) => set((state) => ({
        posts: [
            ...state.posts,
            {
                id: Date.now(), // Unique id based on current timestamp
                description,
                imageUrl,
                username: state.userData.username // Store the username with each post
            }
        ]
    })),

    // Delete post action
    deletePost: (postId) => set((state) => ({
        posts: state.posts.filter((post) => post.id !== postId) // Remove the post by id
    }))
}));

export default useStore;
