import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Toolbar from "../components/Toolbar";

const UserDetailPage = () => {
    const { id } = useParams()
    const [user, setUser] = useState(null)

    useEffect(() => {
        fetch(`https://jsonplaceholder.typicode.com/users/${id}`)
            .then(response => response.json())
            .then(data => {
                setUser(data);
            })
            .catch(error => {
                console.error('Error fetching user details:', error);
            });
    }, [id])


    if (!user) {
        return <p>User not found.</p>
    }

    return (
        <div>
            <h2>
                <Toolbar/>
            </h2>
            <div className="main-user-card d-flex flex-row align-items-center">
                <div>
                    hhhhhhhh
                </div>
            <div className="user-detail-page user-cont">
                <h1>{user.name}</h1>
                <p><strong>Username:</strong> {user.username}</p>
                <p><strong>Email:</strong> {user.email}</p>
                <p><strong>Phone:</strong> {user.phone}</p>
                <p><strong>Website:</strong> <a  className="text-white"  href={`https://${user.website}`} target="_blank"
                                                rel="noopener noreferrer">{user.website}</a></p>
            </div>
            </div>
        </div>
    );
};

export default UserDetailPage;
