import React, { useState, useEffect } from 'react';
import Toolbar from "../components/Toolbar";

const GalleryPage = () => {
    const [photos, setPhotos] = useState([]);
    const [cartTotal, setCartTotal] = useState(0);

    // Fetching data from the API on component mount
    useEffect(() => {
        fetch('http://167.99.138.67:1111/getallposts')
            .then((res) => res.json())
            .then((data) => {
                console.log(data);  // Log the data to check its structure
                setPhotos(data);
            })
            .catch((error) => console.error("Error fetching data:", error));
    }, []);

    const handleAddToCart = (price) => {
        setCartTotal((prevTotal) => prevTotal + price);
    };

    return (
        <div>
            <Toolbar cartTotal={cartTotal} />
            <div className="gallery">
                {photos.length > 0 ? (
                    photos.map((photo, index) => (
                        <div key={index} className="photo-card">
                            <img src={photo.photo_url} alt={photo.title} className="photo-image" />
                            <div className="photo-details">
                                <h3>{photo.title}</h3>
                                <p>{photo.description}</p>
                                <p>By {photo.username}</p>
                                <p>Price: ${photo.price.toFixed(2)}</p>
                                <button onClick={() => handleAddToCart(photo.price)}>
                                    Add to Cart
                                </button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>Loading photos...</p>
                )}
            </div>
        </div>
    );
};

export default GalleryPage;
